import React, { useState } from 'react';
import Deck from "./Deck";
import Card from "./Card";
import IncreaseMat from "./IncreaseMat";
import DecreaseMat from "./DecreaseMat";
import './Board.css';
import PropTypes from 'prop-types';

const Board = () => {
    const [deckMessage, setDeckMessage] = useState('The game has not started.');
    const [cards, setCards] = useState([0, 0, 0, 0, 0, 0, 0]);
    const [selectedCard, setSelectedCard] = useState(null)
    const [selectedCardIndex, setSelectedCardIndex] = useState(null)
    const [mats, setMats] = useState([1, 1, 100, 100])
    const [matMessage, setMatMessage] = useState(null)
    const [deck, setDeck] = useState(null)

    const shuffleDeck = () => {
        let currentIndex = deck.length;
        while (currentIndex != 0) {

          let randomIndex = Math.floor(Math.random() * currentIndex);
          currentIndex--;

          [deck[currentIndex], deck[randomIndex]] = [
            deck[randomIndex], deck[currentIndex]];
        }
    }

    const startGame = () => {
        console.log('started game')
        setDeck(Array.from({length: 98}, (_, i) => i + 2))
        shuffleDeck()
        const newCardValues = [deck[0], deck[1], deck[2], deck[3], deck[4], deck[5], deck[6]];
        setCards(newCardValues);
        deck.splice(0, 7)
        setDeckMessage(`Remaining Cards ${deck.length}`);
        console.log('at end')
        console.log(deck)
    };

    // this isn't working RRRRRRRGH
    const refillCards = () => {
        console.log('at start of refill cards this is the deck:')
        console.log(deck)
        console.log('refill cards')
        let deckCounter = 0
        for(let i = 0; i < cards.length; i++) {
            if(cards[i] == '*') {
                cards[i] = deck[deckCounter++]
            }
        }
        console.log(`deck counter ${deckCounter}`)
        setCards(cards)
        console.log(cards)
        deck.splice(0, deckCounter)
        setDeckMessage(`Remaining Cards ${deck.length}`);
        console.log(deck)
    };

    const handleCardClick = (value) => {
        console.log(`current value ${cards[value]}`)
        setSelectedCard(cards[value])
        setSelectedCardIndex(value)
    }

    const tryApplyIncreasingValue = (selectedCardIndex, matValueIndex) => {
        console.log('trying to apply increase')
        let selectedCard = cards[selectedCardIndex]
        if (selectedCard === '*') {
            console.log('do nothing')
        } else if (selectedCard > mats[matValueIndex]) {
            console.log(`apply selected card ${mats[matValueIndex]}`)
            mats[matValueIndex] = selectedCard
            cards[selectedCardIndex] = '*'
        } else if ((mats[matValueIndex] - 10) === selectedCard) {
            console.log(`10 step down ${mats[matValueIndex]}`)
            mats[matValueIndex] = selectedCard
            cards[selectedCardIndex] = '*'
        } else {
            console.log('bad')
        }

    }

    const tryApplyDecreasingValue = (selectedCardIndex, matValueIndex) => {
        console.log('trying to apply decrease')
        let selectedCard = cards[selectedCardIndex]
        if (selectedCard === '*') {
            console.log('do nothing')
        } else if (selectedCard < mats[matValueIndex]) {
            console.log(`apply selected card ${mats[matValueIndex]}`)
            mats[matValueIndex] = selectedCard
            cards[selectedCardIndex] = '*'
        } else if ((mats[matValueIndex] + 10) === selectedCard) {
            console.log(`10 step down ${mats[matValueIndex]}`)
            mats[matValueIndex] = selectedCard
            cards[selectedCardIndex] = '*'
        } else {
            console.log('bad')
        }

    }

    return (
        <section className="board_background">
            <div className="rules">
                You may only place cards that are greater than the previous card in the ascending deck, and cards less than the previous in the descending deck. The exception to this is if the value of your card is exactly +/- 10 of the topmost card on the pile.
            </div>
            <div>
                <Deck id="deck" message={deckMessage} />
            </div>

            <input type='button' className='card' id="card_1" value={cards[0]} onClick={() => handleCardClick(0)} />
            {/* <div id="card_1"><Card card={cards[0]} onClick={() => handleCardClick(cards[0])} /></div> */}
            <input type='button' className='card' id="card_2" value={cards[1]} onClick={() => handleCardClick(1)} />
            {/* <div id="card_2"><Card card={cards[1]} /></div> */}
            <input type='button' className='card' id="card_3" value={cards[2]} onClick={() => handleCardClick(2)} />
            {/* <div id="card_3"><Card card={cards[2]} /></div> */}
            <input type='button' className='card' id="card_4" value={cards[3]} onClick={() => handleCardClick(3)} />
            {/* <div id="card_4"><Card card={cards[3]} /></div> */}
            <input type='button' className='card' id="card_5" value={cards[4]} onClick={() => handleCardClick(4)} />
            {/* <div id="card_5"><Card card={cards[4]} /></div> */}
            <input type='button' className='card' id="card_6" value={cards[5]} onClick={() => handleCardClick(5)} />
            {/* <div id="card_6"><Card card={cards[5]} /></div> */}
            <input type='button' className='card' id="card_7" value={cards[6]} onClick={() => handleCardClick(6)} />
            {/* <div id="card_7"><Card card={cards[6]} /></div> */}


            <input type='button' className='increase_mat' id='increase_one' value={mats[0]} matMessage={matMessage} onClick={() => tryApplyIncreasingValue(selectedCardIndex, 0)} />
            {/* <div id="increase_one"><IncreaseMat value={mats[0]} matMessage={matMessage} onClick={() => tryApplyIncreasingValue(selectedCard, 0)} /></div> */}
            <input type='button' className='increase_mat' id='increase_two' value={mats[1]} matMessage={matMessage} onClick={() => tryApplyIncreasingValue(selectedCardIndex, 1)} />
            {/* <div id="increase_two"><IncreaseMat value={mats[1]} /></div> */}
            <input type='button' className='decrease_mat' id='decrease_one' value={mats[2]} matMessage={matMessage} onClick={() => tryApplyDecreasingValue(selectedCardIndex, 2)} />
            {/* <div id="decrease_one"><DecreaseMat value={mats[2]} /></div> */}
            <input type='button' className='decrease_mat' id='decrease_two' value={mats[3]} matMessage={matMessage} onClick={() => tryApplyDecreasingValue(selectedCardIndex, 3)} />
            {/* <div id="decrease_two"><DecreaseMat value={mats[3]} /></div> */}

            <input type='button' id='new_game_button' onClick={startGame} value='Start Game' />
            <input type='button' id='refill_cards_button' onClick={refillCards} value='Refill Cards' />
        </section>
    );
};

Board.propTypes = {
    deck: PropTypes.arrayOf(Number),
    increaseMatOne: PropTypes.instanceOf(IncreaseMat),
    increaseMatTwo: PropTypes.instanceOf(IncreaseMat),
    decreaseMatOne: PropTypes.instanceOf(DecreaseMat),
    decreaseMatTwo: PropTypes.instanceOf(DecreaseMat),
};

export default Board;
