import Deck from "./Deck";
import Card from "./Card";
import IncreaseMat from "./IncreaseMat";
import DecreaseMat from "./DecreaseMat";
import './Board.css'
import PropTypes from 'prop-types'
import React, { useState } from 'react'


const Board = () => {
  // State to store the text for the Deck
  const [deckText, setDeckText] = useState('The game has not started.');
  const cards = []

  const shuffleDeck = ( cards ) => {
    let random, hold;
        for (let i = 0; i < this.cards.length; i++) {
            random = Math.floor(Math.random() * (this.cards.length));
            hold = this.cards[i];
            this.cards[i] = this.cards[random];
            this.cards[random] = hold;
        }
  }

  // Function to start the game and update the Deck text
  const startGame = () => {
    for (let i = 2; i < 100; i++) {
        this.cards.push(i)
    }
    setDeckText('The game has started! Good luck!');
  };

  return (
    <section className="board_background">
      <div className="rules">
        You may only place cards that are greater than the previous card in the ascending deck, and cards less than the previous in the descending deck. The exception to this is if the value of your card is exactly +/- 10 of the topmost card on the pile.
      </div>
      <div>
        {/* Pass the deckMessage state to the Deck component */}
        <Deck id="deck" message={deckText} cards={cards} />
      </div>
      <div id="card_1"><Card /></div>
      <div id="card_2"><Card /></div>
      <div id="card_3"><Card /></div>
      <div id="card_4"><Card /></div>
      <div id="card_5"><Card /></div>
      <div id="card_6"><Card /></div>
      <div id="card_7"><Card /></div>
      <div id="increase_one"><IncreaseMat /></div>
      <div id="increase_two"><IncreaseMat /></div>
      <div id="decrease_one"><DecreaseMat /></div>
      <div id="decrease_two"><DecreaseMat /></div>

      {/* Button that triggers the startGame function */}
      <input type='button' id='new_game_button' onClick={startGame} value='Start Game' />
    </section>
  );
};

Board.propTypes = {
  deck: PropTypes.arrayOf(Number),
  cards: PropTypes.arrayOf(PropTypes.instanceOf(Card)),
  increaseMatOne: PropTypes.instanceOf(IncreaseMat),
  increaseMatTwo: PropTypes.instanceOf(IncreaseMat),
  decreaseMatOne: PropTypes.instanceOf(DecreaseMat),
  decreaseMatTwo: PropTypes.instanceOf(DecreaseMat),
};

export default Board;
