import Deck from "./Deck";
import Card from "./Card";
import IncreaseMat from "./IncreaseMat";
import DecreaseMat from "./DecreaseMat";
import './Board.css'
import PropTypes from 'prop-types'
import React, { useState } from 'react'



const Board = () => {
    const [deckText, setDeckText] = useState('Deck')

    const startGame = () => {
        setDeckText('Game has started!')
    }

    return (
        <section className="board_background">
            <div className="rules"> You may only place cards that are greater than the previous card in the ascending deck, and cards less than the previous in the descending deck.
                The exception to this is if the value of your card is exactly +/- 10 of the topmost card on the pile.
            </div>
            <div> <Deck id="deck" message={deckText}/> </div>
            <div id="card_1"> <Card /> </div>
            <div id="card_2"> <Card /> </div>
            <div id="card_3"> <Card /> </div>
            <div id="card_4"> <Card /> </div>
            <div id="card_5"> <Card /> </div>
            <div id="card_6"> <Card /> </div>
            <div id="card_7"> <Card /> </div>
            <div id="increase_one"> <IncreaseMat /></div>
            <div id="increase_two"><IncreaseMat /></div>
            <div id="decrease_one"> <DecreaseMat /></div>
            <div id="decrease_two"> <DecreaseMat /></div>
            <input type='button' id='new_game_button' onClick={startGame} value='Start Game' />

        </section>

    )
}

Board.propTypes = {
    deck: PropTypes.arrayOf(Number),
    cards: PropTypes.arrayOf(PropTypes.instanceOf(Card)),
    increaseMatOne: PropTypes.instanceOf(IncreaseMat),
    increaseMatTwo: PropTypes.instanceOf(IncreaseMat),
    decreaseMatOne: PropTypes.instanceOf(DecreaseMat),
    decreaseMatTwo: PropTypes.instanceOf(DecreaseMat),
}

export default Board;
