import './Deck.css'
import Card from './Card';


const Deck = () => {
    let cards = []
    let hands = []

    function createDeck() {
        for (let i = 2; i < 100; i++) {
            this.cards.push(new Card(i))
        }
    }
    function shuffleDeck() {
        let random, hold;
        for (let i = 0; i < this.cards.length; i++) {
            random = Math.floor(Math.random() * (this.cards.length));
            hold = this.cards[i];
            this.cards[i] = this.cards[random];
            this.cards[random] = hold;
        }
    }

    function dealCards(players) {
        // this.shuffleDeck()
        // let players = this.players.length
        for (let i = 0; i < players; i++) {
            let temp = []
            let counter = i
            while (temp.length < 7) {
                temp.push(this.cards[counter])
                counter += players
            }
            this.hands.push(temp)
        }

        this.cards = this.cards.slice(players * 7)

        for (let i = 0; i < this.hands.length; i++) {
            console.log(`Player ${i + 1}, you have ${this.hands[i].length} cards`)
            console.log(`your hand is: ${this.formatPrint(this.hands[i])}`)
        }
        console.log('-------------------------------')

    }

    function     refillCards() {
        //just for checking
        for (let i = 0; i < this.hands.length; i++) {
            this.hands[i].shift()
            this.hands[i].shift()
            console.log(this.hands[i])
        }

        for (let i = 0; i < this.hands.length; i++) {
            let numCards = this.hands[i].length
            while (numCards < 7) {
                this.hands[i].push(this.cards[0])
                this.cards.shift()
                numCards = this.hands[i].length
            }
        }
        for (let i = 0; i < this.hands.length; i++) {
            console.log(`Player ${i + 1}, you have ${this.hands[i].length} cards`)
            console.log(`your hand is: ${this.formatPrint(this.hands[i])}`)
        }
        console.log('-------------------------------')
    }

    return (
        <div className="deck"> Deck
        </div>
    )
}

export default Deck;
