import React, { useState } from 'react';
import Deck from "./Deck";
import Card from "./Card";
import IncreaseMat from "./IncreaseMat";
import DecreaseMat from "./DecreaseMat";
import './Board.css';
import PropTypes from 'prop-types';

const Board = () => {
  // State to store the deck message
  const [deckMessage, setDeckMessage] = useState('The game has not started.');

  // State to store values for each card (array of card values)
  const [cards, setCards] = useState(['0', '0', '0', '0', '0', '0', '0']); // Initial card values

  // Function to start the game and update card values
  const startGame = () => {
    setDeckMessage('The game has started! Good luck!');

    // Generate new card values for each card (example: random numbers for demonstration)
    const newCardValues = [
      Math.floor(Math.random() * 100), // Card 1
      Math.floor(Math.random() * 100), // Card 2
      Math.floor(Math.random() * 100), // Card 3
      Math.floor(Math.random() * 100), // Card 4
      Math.floor(Math.random() * 100), // Card 5
      Math.floor(Math.random() * 100), // Card 6
      Math.floor(Math.random() * 100), // Card 7
    ];

    setCards(newCardValues); // Update state with new card values
  };

  return (
    <section className="board_background">
      <div className="rules">
        You may only place cards that are greater than the previous card in the ascending deck, and cards less than the previous in the descending deck. The exception to this is if the value of your card is exactly +/- 10 of the topmost card on the pile.
      </div>
      <div>
        <Deck id="deck" message={deckMessage} />
      </div>

      {/* Render each Card with a unique value from the 'cards' array */}
      <div id="card_one"><Card id="card_1" card={cards[0]} /></div>
      <div id="card_2"><Card card={cards[1]} /></div>
      <div id="card_3"><Card card={cards[2]} /></div>
      <div id="card_4"><Card card={cards[3]} /></div>
      <div id="card_5"><Card card={cards[4]} /></div>
      <div id="card_6"><Card card={cards[5]} /></div>
      <div id="card_7"><Card card={cards[6]} /></div>

      <div id="increase_one"><IncreaseMat /></div>
      <div id="increase_two"><IncreaseMat /></div>
      <div id="decrease_one"><DecreaseMat /></div>
      <div id="decrease_two"><DecreaseMat /></div>

      <input type='button' id='new_game_button' onClick={startGame} value='Start Game' />
    </section>
  );
};

Board.propTypes = {
  deck: PropTypes.arrayOf(Number),
  cards: PropTypes.arrayOf(PropTypes.instanceOf(Card)),
  increaseMatOne: PropTypes.instanceOf(IncreaseMat),
  increaseMatTwo: PropTypes.instanceOf(IncreaseMat),
  decreaseMatOne: PropTypes.instanceOf(DecreaseMat),
  decreaseMatTwo: PropTypes.instanceOf(DecreaseMat),
};

export default Board;
